import React, { useEffect, useState, useCallback } from 'react';
import { Line } from 'react-chartjs-2';
import { Chart as ChartJS, CategoryScale, LinearScale, PointElement, LineElement, Title, Tooltip, Legend } from 'chart.js';
import { SimulateRealTimeData } from './SimulateRealTimeData.tsx';
import { DateRange } from 'react-date-range';
import 'react-date-range/dist/styles.css';
import 'react-date-range/dist/theme/default.css';

// Define the type for our sensor data
interface SensorData {
  sensorId: string;
  timestamp: string;
  airQuality: number;
  temperature: number;
  humidity: number;
}

ChartJS.register(CategoryScale, LinearScale, PointElement, LineElement, Title, Tooltip, Legend);  //ensure to register for rendering

export default function SensorChart() {
    const [filters, setFilters] = useState({
        temperature: true,
        humidity: true,
        airQuality: true
    });

    const [dateRange, setDateRange] = useState({
        startDate: new Date(Date.now() - 7 * 24 * 60 * 60 * 1000), // 7 days ago
        endDate: new Date(),
        key: 'selection'
    });

    const [showDateRange, setShowDateRange] = useState(false);
    const [sensorData, setSensorData] = useState<SensorData[]>([]);

    const formatTime = (date = new Date()) => {
        return date.toLocaleTimeString('en-US', {
            hour12: false,
            hour: '2-digit',
            minute: '2-digit',
            second: '2-digit'
        });
    };

    const [chartData, setChartData] = useState({  //this is the data that is displayed on the chart
      labels: Array(7).fill('').map(() => formatTime()),
      datasets: [
        {
          label: 'Temperature (°C)',
          data: [],
          borderColor: 'rgb(0, 0, 139)', // Dark blue color
          tension: 0.1
        },
        {
          label: 'Humidity (%)',
          data: [],
          borderColor: 'rgb(255, 99, 132)',
          tension: 0.1
        },
        {
          label: 'Air Quality (AQI)',
          data: [],
          borderColor: 'rgb(53, 162, 235)',
          tension: 0.1
        }
      ]
    });

    // Callback to handle updates from the simulator
    const handleDataUpdate = useCallback((updates: SensorData[]) => {
      setSensorData(prev => [...prev, ...updates].slice(-20)); // Keep last 20 updates

      // Update chart data with the latest readings
      if (updates.length > 0) {
        const latest = updates[updates.length - 1];
        const now = new Date();

        setChartData(prev => {
          // Add new time to labels, keeping only last 7 entries
          const newLabels = [...prev.labels.slice(1), formatTime(now)];

          return {
            ...prev,
            labels: newLabels,
            datasets: [
              {
                ...prev.datasets[0],
                data: [...prev.datasets[0].data.slice(-6), latest.temperature]
              },
              {
                ...prev.datasets[1],
                data: [...prev.datasets[1].data.slice(-6), latest.humidity]
              },
              {
                ...prev.datasets[2],
                data: [...prev.datasets[2].data.slice(-6), latest.airQuality]
              }
            ]
          };
        });
      }
    }, []);

    // Set up the data simulation when component mounts
    useEffect(() => {
      // Start simulation with 1 sensor, updating every 2 seconds
      const cleanup = SimulateRealTimeData(1, 2000, handleDataUpdate);

      // Clean up interval on unmount
      return () => {
        cleanup && cleanup();
      };
    }, [handleDataUpdate]);

    const options = {
      responsive: true,
      animation: {
        duration: 1000
      },
      scales: {
        y: {
          beginAtZero: false
        }
      }
    };

    const handleFilterChange = (e) => {
      const { name, checked } = e.target;
      setFilters(prev => ({
        ...prev,
        [name]: checked
      }));
    };

    const filteredChartData = {
      ...chartData,
      datasets: chartData.datasets.filter(dataset => 
        (dataset.label.includes('Temperature') && filters.temperature) ||
        (dataset.label.includes('Humidity') && filters.humidity) ||
        (dataset.label.includes('Air Quality') && filters.airQuality)
      )
    };

    return (
      <div className="chart-container">
        <div className="chart-filters">
          <div className="filter-group">
            <label>
              <input
                type="checkbox"
                name="temperature"
                checked={filters.temperature}
                onChange={handleFilterChange}
              />
              Temperature
            </label>
            <label>
              <input
                type="checkbox"
                name="humidity"
                checked={filters.humidity}
                onChange={handleFilterChange}
              />
              Humidity
            </label>
            <label>
              <input
                type="checkbox"
                name="airQuality"
                checked={filters.airQuality}
                onChange={handleFilterChange}
              />
              Air Quality
            </label>
          </div>
          <div className="date-range-container">
            <button 
              className="date-range-button"
              onClick={() => setShowDateRange(!showDateRange)}
            >
              Select Date Range
            </button>
            {showDateRange && (
              <div className="date-range-picker">
                <DateRange
                  editableDateInputs={true}
                  onChange={item => setDateRange(item.selection)}
                  moveRangeOnFirstSelection={false}
                  ranges={[dateRange]}
                />
                <button 
                  className="apply-button"
                  onClick={() => {
                    // Filter data based on date range
                    // This is where you would filter your sensorData
                    setShowDateRange(false);
                  }}
                >
                  Apply
                </button>
              </div>
            )}
          </div>
        </div>
        <div className="chart-wrapper">
          <h1 style={{ color: "black" }}>Real-time Sensor Data</h1>
          <Line data={filteredChartData} options={options} />
        </div>
      </div>
    );
}
