import React, { useEffect, useState, useCallback, useRef } from 'react';
import { SimulateRealTimeData } from './SimulateRealTimeData.tsx';

// Define the type for our sensor data
interface SensorData {
  sensorId: string;
  timestamp: string;
  airQuality: number;
  temperature: number;
  humidity: number;
}

interface Point {
  x: number;
  y: number;
}

// Chart configuration
const CHART_CONFIG = {
  width: 800,
  height: 400,
  margin: { top: 20, right: 30, bottom: 40, left: 50 },
  colors: {
    temperature: '#ff6384',
    humidity: '#36a2eb',
    airQuality: '#4bc0c0',
    grid: '#e0e0e0',
    text: '#333'
  }
};

export default function SensorChart() {
  const [data, setData] = useState<SensorData[]>([]);
  const [filters, setFilters] = useState({
    temperature: true,
    humidity: true,
    airQuality: true
  });
  const chartRef = useRef<HTMLDivElement>(null);
  const [dimensions, setDimensions] = useState({ width: CHART_CONFIG.width, height: CHART_CONFIG.height });

  // Handle window resize
  useEffect(() => {
    const handleResize = () => {
      if (chartRef.current) {
        const width = Math.min(window.innerWidth - 40, CHART_CONFIG.width);
        setDimensions({
          width,
          height: CHART_CONFIG.height * (width / CHART_CONFIG.width)
        });
      }
    };

    handleResize();
    window.addEventListener('resize', handleResize);
    return () => window.removeEventListener('resize', handleResize);
  }, []);

  const formatTime = (date = new Date()) => {
    return date.toLocaleTimeString('en-US', {
      hour12: false,
      hour: '2-digit',
      minute: '2-digit',
      second: '2-digit'
    });
  };

  // Process data for the chart
  const processData = useCallback((newData: SensorData) => {
    setData(prev => {
      const updated = [...prev, newData].slice(-30); // Keep last 30 data points
      return updated;
    });
  }, []);

  // Generate path data for a line
  const generatePath = (points: Point[]) => {
    if (points.length < 2) return '';

    let path = `M ${points[0].x},${points[0].y}`;

    for (let i = 1; i < points.length; i++) {
      const x = points[i].x;
      const y = points[i].y;
      path += ` L ${x},${y}`;
    }

    return path;
  };

  // Calculate chart dimensions
  const chartWidth = dimensions.width - CHART_CONFIG.margin.left - CHART_CONFIG.margin.right;
  const chartHeight = dimensions.height - CHART_CONFIG.margin.top - CHART_CONFIG.margin.bottom;

  // Calculate scales
  const xScale = (value: number, index: number) => 
    (index / Math.max(1, data.length - 1)) * chartWidth;

  const yScale = (value: number, min: number, max: number) => 
    chartHeight - ((value - min) / (max - min || 1)) * chartHeight;

  // Get min/max values for scaling
  const getMinMax = (key: 'temperature' | 'humidity' | 'airQuality') => {
    if (data.length === 0) return { min: 0, max: 100 };
    const values = data.map(d => d[key]).filter(Number.isFinite) as number[];
    if (values.length === 0) return { min: 0, max: 100 };
    return {
      min: Math.min(...values, 0),
      max: Math.max(...values, 100)
    };
  };

  // Generate grid lines
  const generateGridLines = (count: number, isHorizontal: boolean) => {
    const lines = [];
    const step = isHorizontal ? chartHeight / count : chartWidth / count;

    for (let i = 0; i <= count; i++) {
      const pos = i * step;
      lines.push(
        isHorizontal ? (
          <line
            key={`h-line-${i}`}
            x1={0}
            y1={pos}
            x2={chartWidth}
            y2={pos}
            stroke={CHART_CONFIG.colors.grid}
            strokeWidth="1"
            strokeDasharray="2,2"
          />
        ) : (
          <line
            key={`v-line-${i}`}
            x1={pos}
            y1={0}
            x2={pos}
            y2={chartHeight}
            stroke={CHART_CONFIG.colors.grid}
            strokeWidth="1"
            strokeDasharray="2,2"
          />
        )
      );
    }
    return lines;
  };

  // Generate data points for each metric
  const getDataPoints = (key: 'temperature' | 'humidity' | 'airQuality') => {
    const { min, max } = getMinMax(key);
    return data.map((d, i) => ({
      x: xScale(0, i),
      y: yScale(d[key] as number, min, max)
    }));
  };

  // Toggle filter
  const toggleFilter = (key: keyof typeof filters) => {
    setFilters(prev => ({
      ...prev,
      [key]: !prev[key]
    }));
  };

  // Simulate real-time data updates
  useEffect(() => {
    const handleNewData = (newData: Omit<SensorData, 'timestamp'>) => {
      processData({
        ...newData,
        timestamp: formatTime(new Date())
      });
    };

    // Start the real-time data simulation with required parameters
    const cleanup = SimulateRealTimeData(1, 1000, handleNewData);

    // Cleanup function to stop the simulation when the component unmounts
    return () => {
      if (cleanup) cleanup();
    };
  }, [processData]);

  return (
    <div className="p-4 bg-gray-800 rounded-lg shadow-lg" ref={chartRef}>
      <div className="flex flex-wrap gap-4 mb-4">
        <button
          onClick={() => toggleFilter('temperature')}
          className={`px-4 py-2 rounded-md ${filters.temperature ? 'bg-blue-600 text-white' : 'bg-gray-200 text-gray-800'}`}
        >
          Temperature
        </button>
        <button
          onClick={() => toggleFilter('humidity')}
          className={`px-4 py-2 rounded-md ${filters.humidity ? 'bg-blue-600 text-white' : 'bg-gray-200 text-gray-800'}`}
        >
          Humidity
        </button>
        <button
          onClick={() => toggleFilter('airQuality')}
          className={`px-4 py-2 rounded-md ${filters.airQuality ? 'bg-blue-600 text-white' : 'bg-gray-200 text-gray-800'}`}
        >
          Air Quality
        </button>
      </div>

      <div className="relative" style={{ width: '100%', height: dimensions.height }}>
        <svg width="100%" height="100%" viewBox={`0 0 ${dimensions.width} ${dimensions.height}`}>
          {/* Background */}
          <rect width="100%" height="100%" fill="#1f2937" />

          {/* Chart area */}
          <g transform={`translate(${CHART_CONFIG.margin.left}, ${CHART_CONFIG.margin.top})`}>
            {/* Grid lines */}
            <g className="grid-lines">
              {generateGridLines(5, true).map((line, i) => (
                <React.Fragment key={`grid-${i}`}>
                  {line}
                </React.Fragment>
              ))}
              {generateGridLines(10, false).map((line, i) => (
                <React.Fragment key={`v-grid-${i}`}>
                  {line}
                </React.Fragment>
              ))}
            </g>

            {/* Axes */}
            <line
              x1={0}
              y1={chartHeight}
              x2={chartWidth}
              y2={chartHeight}
              stroke="white"
              strokeWidth="1"
            />
            <line
              x1={0}
              y1={0}
              x2={0}
              y2={chartHeight}
              stroke="white"
              strokeWidth="1"
            />

            {/* Data lines */}
            {filters.temperature && data.length > 0 && (
              <path
                d={generatePath(getDataPoints('temperature'))}
                fill="none"
                stroke={CHART_CONFIG.colors.temperature}
                strokeWidth="2"
                strokeLinejoin="round"
                strokeLinecap="round"
              />
            )}

            {filters.humidity && data.length > 0 && (
              <path
                d={generatePath(getDataPoints('humidity'))}
                fill="none"
                stroke={CHART_CONFIG.colors.humidity}
                strokeWidth="2"
                strokeLinejoin="round"
                strokeLinecap="round"
              />
            )}

            {filters.airQuality && data.length > 0 && (
              <path
                d={generatePath(getDataPoints('airQuality'))}
                fill="none"
                stroke={CHART_CONFIG.colors.airQuality}
                strokeWidth="2"
                strokeLinejoin="round"
                strokeLinecap="round"
              />
            )}

            {/* Data points */}
            {filters.temperature && data.map((point, i) => {
              const points = getDataPoints('temperature');
              if (!points[i]) return null;
              return (
                <circle
                  key={`temp-${i}`}
                  cx={points[i].x}
                  cy={points[i].y}
                  r={3}
                  fill={CHART_CONFIG.colors.temperature}
                  stroke="white"
                  strokeWidth="1"
                />
              );
            })}

            {/* Legend */}
            <g transform={`translate(${chartWidth - 150}, 20)`}>
              {filters.temperature && (
                <g>
                  <line x1={0} y1={5} x2={20} y2={5} stroke={CHART_CONFIG.colors.temperature} strokeWidth="2" />
                  <text x={30} y={9} fill="white" fontSize="12">Temperature (Â°C)</text>
                </g>
              )}
              {filters.humidity && (
                <g transform={`translate(0, ${filters.temperature ? 20 : 0})`}>
                  <line x1={0} y1={5} x2={20} y2={5} stroke={CHART_CONFIG.colors.humidity} strokeWidth="2" />
                  <text x={30} y={9} fill="white" fontSize="12">Humidity (%)</text>
                </g>
              )}
              {filters.airQuality && (
                <g transform={`translate(0, ${(filters.temperature ? 20 : 0) + (filters.humidity ? 20 : 0)})`}>
                  <line x1={0} y1={5} x2={20} y2={5} stroke={CHART_CONFIG.colors.airQuality} strokeWidth="2" />
                  <text x={30} y={9} fill="white" fontSize="12">Air Quality (AQI)</text>
                </g>
              )}
            </g>
          </g>

          {/* X-axis labels */}
          <g transform={`translate(${CHART_CONFIG.margin.left}, ${dimensions.height - CHART_CONFIG.margin.bottom})`}>
            {data.length > 0 && (
              <text
                x={chartWidth / 2}
                y={30}
                textAnchor="middle"
                fill="white"
                fontSize="12"
              >
                Time
              </text>
            )}

            {data.length > 4 && (
              <>
                <text x={0} y={20} fill="white" fontSize="10" textAnchor="start">
                  {data[0]?.timestamp || ''}
                </text>
                <text x={chartWidth} y={20} fill="white" fontSize="10" textAnchor="end">
                  {data[data.length - 1]?.timestamp || ''}
                </text>
              </>
            )}
          </g>

          {/* Y-axis labels */}
          <g transform={`translate(${CHART_CONFIG.margin.left}, ${CHART_CONFIG.margin.top})`}>
            {data.length > 0 && (
              <text
                x={-30}
                y={-10}
                textAnchor="middle"
                fill="white"
                fontSize="12"
                transform="rotate(-90, -30, -10)"
              >
                Value
              </text>
            )}

            {[0, 0.25, 0.5, 0.75, 1].map((t, i) => {
              if (data.length === 0) return null;
              const tempRange = getMinMax('temperature');
              const value = Math.round(tempRange.min + (1 - t) * (tempRange.max - tempRange.min));
              return (
                <text
                  key={`y-label-${i}`}
                  x={-10}
                  y={t * chartHeight + 3}
                  textAnchor="end"
                  fill="white"
                  fontSize="10"
                >
                  {value}
                </text>
              );
            })}
          </g>
        </svg>
      </div>
    </div>
  );
}
